import java.util.Scanner;class Main{public static void main(String[]args){Scanner s=new Scanner(System.in);System.out.println("Enter three points for a triangle.");System.out.print("(x1, y1):");int x1=s.nextInt();int y1=s.nextInt();System.out.print("(x2, y2):");int x2=s.nextInt();int y2=s.nextInt();System.out.print("(x3, y3):");int x3=s.nextInt();int y3=s.nextInt();int side1=(int)Math.sqrt((x2-x1)*(x2-x1)+(y2-y1)*(y2-y1));int side2=(int)Math.sqrt((x3-x2)*(x3-x2)+(y3-y2)*(y3-y2));int side3=(int)Math.sqrt((x3-x1)*(x3-x1)+(y3-y1)*(y3-y1));int sPerimeter=(side1+side2+side3)/2;double area=Math.sqrt(sPerimeter*(sPerimeter-side1)*(sPerimeter-side2)*(sPerimeter-side3));System.out.println("The area of the triangle is "+area);} }
